// Оголошення API_URL
const API_URL = 'http://localhost:5004';  // Це URL вашого сервера, змініть на потрібний

// Функція для отримання всіх машин
export const fetchCars = async () => {
  try {
    const response = await axios.get(`${API_URL}/cars`);
    return response.data;
  } catch (error) {
    console.error('Помилка при отриманні даних:', error);
    return [];  // Якщо є помилка, повертаємо порожній масив
  }
};

// Функція для отримання деталей конкретного автомобіля
export const fetchCarDetails = async (id) => {
  try {
    const response = await axios.get(`${API_URL}/cars/${id}`);  // Використовуємо правильний шлях
    return response.data;
  } catch (error) {
    console.error('Помилка при отриманні даних автомобіля:', error);
    return null;  // Якщо автомобіль не знайдено, повертаємо null
  }
};

// Функція для додавання до кошика
export const addToCart = async (carData) => {
  const response = await axios.post(`${API_URL}/cart`, carData);
  return response.data;
};

// Функція для отримання кошика
export const fetchCart = async () => {
  const response = await axios.get(`${API_URL}/cart`);
  return response.data;
};

// Функція для очищення кошика
export const clearCart = async () => {
  const response = await axios.delete(`${API_URL}/cart`);
  return response.data;
};
