import React, { useContext, useState } from 'react';
import { CarContext } from './carcontext';
import CarCard from './carcrad';
import Spinner from './spinner';
import './catalog.css';

const Catalog = () => {
  const { carList, totalSpeed, handleSearch, handleSortAsc, handleSortDesc, handleTotalSpeed, isLoading } = useContext(CarContext);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredCars, setFilteredCars] = useState(carList);

  React.useEffect(() => {
    setFilteredCars(carList);
  }, [carList]);

  const handleSearchChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);

    if (value) {
      const filtered = carList.filter(car =>
        car.brand.toLowerCase().includes(value.toLowerCase())
      );
      setFilteredCars(filtered);
    } else {
      setFilteredCars(carList);
    }
  };

  if (isLoading) {
    return <Spinner />;
  }

  return (
    <div className="catalog-page">
      <div className="catalog-grid">
        {filteredCars.map((car) => (
          <CarCard key={car.id} car={car} />
        ))}
      </div>
    </div>
  );
};

export default Catalog;
