import React, { useState, useEffect } from 'react';
import { fetchCars, fetchCarDetails } from './api';

const CarCatalog = () => {
  const [cars, setCars] = useState([]);
  const [selectedCar, setSelectedCar] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const loadCars = async () => {
      const data = await fetchCars();
      setCars(data);
    };

    loadCars();
  }, []);

  // Фільтрація автомобілів по пошуковому терміну
  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  // Вибір конкретного автомобіля для перегляду
  const handleCarClick = async (id) => {
    const carDetails = await fetchCarDetails(id);
    setSelectedCar(carDetails);
  };

  const filterCars = () => {
    return cars.filter((car) =>
      car.brand.toLowerCase().includes(searchTerm.toLowerCase()) ||
      car.model.toLowerCase().includes(searchTerm.toLowerCase())
    );
  };

  return (
    <div>
      <h2>Каталог автомобілів</h2>

      {/* Панель пошуку */}
      <input
        type="text"
        placeholder="Пошук..."
        value={searchTerm}
        onChange={handleSearchChange}
      />

      {/* Список автомобілів */}
      <div>
        {filterCars().length > 0 ? (
          filterCars().map((car) => (
            <div key={car.id} onClick={() => handleCarClick(car.id)}>
              <h3>{car.brand} {car.model}</h3>
              <img src={`http://localhost:5004${car.image}`} alt={car.model} />
            </div>
          ))
        ) : (
          <p>Нічого не знайдено</p>
        )}
      </div>

      {/* Деталі вибраного автомобіля */}
      {selectedCar && (
        <div>
          <h2>{selectedCar.brand} {selectedCar.model}</h2>
          <p>Потужність: {selectedCar.power} hp</p>
          <p>Макс. швидкість: {selectedCar.maxSpeed} км/год</p>
          <img src={`http://localhost:5004${selectedCar.image}`} alt={selectedCar.model} />
        </div>
      )}
    </div>
  );
};

export default CarCatalog;
