import React, { useEffect, useState } from 'react';
import { fetchCartItems, removeFromCart, updateCartQuantity } from './api';
import './Cart.css';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    const loadCartItems = async () => setCartItems(await fetchCartItems());
    loadCartItems();
  }, []);

  useEffect(() => {
    setTotalPrice(cartItems.reduce((sum, item) => sum + item.price * item.count, 0));
  }, [cartItems]);

  const handleRemove = async id => setCartItems(await removeFromCart(id));

  const handleQuantityChange = async (id, count) => {
    if (count > 0) setCartItems(await updateCartQuantity({ id, count }));
  };

  return (
    <div className="cart-page">
      <h2>Кошик</h2>
      {cartItems.length > 0 ? (
        cartItems.map(item => (
          <div className="cart-item" key={item.id}>
            <img src={`http://127.0.0.1:5005${item.image}`} alt={item.model} />
            <div>
              <h3>{item.brand} {item.model}</h3>
              <p>Ціна: {item.price} грн</p>
              <div>
                <button onClick={() => handleQuantityChange(item.id, item.count - 1)}>-</button>
                <span>{item.count}</span>
                <button onClick={() => handleQuantityChange(item.id, item.count + 1)}>+</button>
              </div>
            </div>
            <button onClick={() => handleRemove(item.id)}>Видалити</button>
          </div>
        ))
      ) : (
        <p>Ваш кошик порожній</p>
      )}
      <div className="total-container">
        Загальна сума: {totalPrice} грн
      </div>
      <button onClick={() => alert('Дякуємо за покупку!')}>Купити</button>
    </div>
  );
};

export default Cart;
