import axios from 'axios';

const API_URL = 'http://127.0.0.1:5005';

export const fetchCars = async () => (await axios.get(`${API_URL}/cars`)).data;

export const fetchCarDetails = async id => (await axios.get(`${API_URL}/cars/${id}`)).data;

export const addToCart = async data => (await axios.post(`${API_URL}/cart`, data)).data;

export const fetchCartItems = async () => (await axios.get(`${API_URL}/cart`)).data;

export const updateCartQuantity = async data => (await axios.put(`${API_URL}/cart/${data.id}`, { count: data.count })).data;

export const removeFromCart = async id => (await axios.delete(`${API_URL}/cart/${id}`)).data;
