import React, { useState, useEffect } from 'react';
import { fetchCars } from './api';  // Імпортуємо функцію API
import './catalog.css';

const Catalog = () => {
  const [cars, setCars] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortOrder, setSortOrder] = useState('');  // Параметр для сортування

  useEffect(() => {
    const loadCars = async () => {
      const data = await fetchCars(searchTerm, sortOrder);  // Пошук і сортування
      setCars(data);
    };

    loadCars();
  }, [searchTerm, sortOrder]);

  // Функція для сортування
  const handleSort = (order) => {
    setSortOrder(order);
  };

  // Фільтрація автомобілів
  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  return (
    <div className="container">
      <h2>Каталог автомобілів</h2>

      {/* Панель пошуку */}
      <input
        type="text"
        placeholder="Пошук..."
        value={searchTerm}
        onChange={handleSearchChange}
      />

      {/* Кнопки сортування */}
      <div className="controls">
        <button onClick={() => handleSort('asc')}>Сортувати за зростанням</button>
        <button onClick={() => handleSort('desc')}>Сортувати за спаданням</button>
      </div>

      {/* Відображення автомобілів */}
      <div className="catalog-grid">
        {cars.length > 0 ? (
          cars.map((car) => (
            <div key={car.id} className="car-card">
              <h3>{car.brand} {car.model}</h3>
              <p>Макс. швидкість: {car.maxSpeed} км/год</p>
              <img src={`http://localhost:5008${car.image}`} alt={car.model} />
            </div>
          ))
        ) : (
          <p>Нічого не знайдено</p>
        )}
      </div>
    </div>
  );
};

export default Catalog;
