import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { fetchCars } from './api';
import { debounce } from 'lodash';
import './catalog.css';

const Catalog = () => {
  const [cars, setCars] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [totalSpeed, setTotalSpeed] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    const loadCars = async () => {
      const data = await fetchCars(searchTerm);
      setCars(data);
    };
    loadCars();
  }, [searchTerm]);

  const handleSearchChange = debounce((e) => {
    setSearchTerm(e.target.value);
  }, 300);

  const calculateTotalSpeed = () => {
    const total = cars.reduce((sum, car) => sum + car.maxSpeed, 0);
    setTotalSpeed(total);
  };

  const handleCarClick = (carId) => {
    navigate(`/item/${carId}`);
  };

  return (
    <div className="container">
      <h2>Каталог автомобілів</h2>
      <input type="text" placeholder="Пошук..." onChange={handleSearchChange} />
      <div className="catalog-grid">
        {cars.length > 0 ? (
          cars.map((car) => (
            <div key={car.id} className="car-card" onClick={() => handleCarClick(car.id)}>
              <h3>{car.brand} {car.model}</h3>
              <p>Макс. швидкість: {car.maxSpeed} км/год</p>
              <img src={`http://localhost:5008${car.image}`} alt={car.model} />
            </div>
          ))
        ) : (
          <p>Нічого не знайдено</p>
        )}
      </div>
      <button onClick={calculateTotalSpeed}>Підрахувати загальну швидкість</button>
      <div>Загальна швидкість: {totalSpeed} км/год</div>
    </div>
  );
};

export default Catalog;
