import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { fetchCarDetails } from './api';
import './item.css';

const Item = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [car, setCar] = useState(null);

  useEffect(() => {
    const loadCarDetails = async () => {
      const data = await fetchCarDetails(id);
      if (data) {
        setCar(data);
      }
    };
    loadCarDetails();
  }, [id]);

  if (!car) {
    return <div>Машина не знайдена</div>;
  }

  return (
    <div className="car-details-page">
      <h2>{car.brand} {car.model}</h2>
      <img src={`http://localhost:5008${car.image}`} alt={car.model} />
      <p>Потужність: {car.power} hp</p>
      <p>Макс. швидкість: {car.maxSpeed} км/год</p>
      <button onClick={() => navigate(-1)}>Назад</button>
    </div>
  );
};

export default Item;
