import React, { useContext, useState } from 'react';
import { CarContext } from './carcontext';
import CarCard from './carcrad';
import Spinner from './spinner';
import './catalog.css';
import './button.css';
import './footer.css';

const Catalog = () => {
  const { carList, totalSpeed, handleSearch, handleSortAsc, handleSortDesc, handleTotalSpeed, isLoading } = useContext(CarContext);
  const [searchTerm, setSearchTerm] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [filteredCars, setFilteredCars] = useState(carList); // Додаємо стан для фільтрованих машин

  // Використовуємо carList, коли компонент рендериться
  React.useEffect(() => {
    setFilteredCars(carList);
  }, [carList]);

  const handleSearchChange = (e) => {
    const value = e.target.value;
    setSearchTerm(value);

    // Фільтруємо пропозиції для автозаповнення
    if (value) {
      const filteredSuggestions = carList.filter(car =>
        car.brand.toLowerCase().startsWith(value.toLowerCase())
      );
      setSuggestions(filteredSuggestions);
    } else {
      setSuggestions([]);
      setFilteredCars(carList); // Показуємо всі машини, якщо поле пошуку порожнє
    }
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    const filtered = carList.filter(car =>
      car.brand.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredCars(filtered); // Оновлюємо список для відображення
    setSuggestions([]); // Очищаємо пропозиції після пошуку
  };

  const handleSuggestionClick = (suggestion) => {
    setSearchTerm(suggestion.brand);
    setFilteredCars([suggestion]); // Показуємо вибрану машину
    setSuggestions([]);
  };

  const clearSearch = () => {
    setSearchTerm('');
    setFilteredCars(carList);
  };

  if (isLoading) {
    return <Spinner />;
  }

  return (
    <div className="catalog-page">
      <div className="controls">
        <button onClick={handleSortAsc}>Sort by Power (down)</button>
        <button onClick={handleSortDesc}>Sort by Power (up)</button>

        <form onSubmit={handleSearchSubmit}>
          <input
            type="text"
            placeholder="Search by brand"
            value={searchTerm}
            onChange={handleSearchChange}
          />
          <button type="submit">Search</button>
          <button type="button" onClick={clearSearch}>Clear</button>
        </form>

        {suggestions.length > 0 && (
          <div className="suggestions">
            {suggestions.map((suggestion, index) => (
              <div
                key={index}
                onClick={() => handleSuggestionClick(suggestion)}
                className="suggestion-item"
              >
                {suggestion.brand}
              </div>
            ))}
          </div>
        )}

        <button onClick={handleTotalSpeed}>Count Total Speed</button>
      </div>

      <div id="total-speed-container">
        <h3>Total Max Speed of Cars in Catalog: <span>{totalSpeed}</span> km/h</h3>
      </div>

      <div className="catalog-grid">
        {filteredCars.map((car, index) => (
          <CarCard key={car.id || index} car={car} />
        ))}
      </div>
    </div>
  );
};

export default Catalog;
