import React, { useState, useEffect } from 'react';
import { fetchCars } from './api';  // Імпортуємо функції API
import { debounce } from 'lodash';  // Імпортуємо функцію debounce
import './catalog.css';

const Catalog = () => {
  const [cars, setCars] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredCars, setFilteredCars] = useState([]);

  useEffect(() => {
    const loadCars = async () => {
      const data = await fetchCars(searchTerm);  // Отримуємо автомобілі на основі пошукового терміну
      setFilteredCars(data);  // Оновлюємо фільтровані автомобілі
    };

    if (searchTerm) {
      loadCars();  // Якщо пошуковий термін не порожній, здійснюємо запит
    } else {
      setFilteredCars([]);  // Якщо немає терміну, очищаємо фільтри
    }
  }, [searchTerm]);  // Викликається кожного разу, коли searchTerm змінюється

  const handleSearchChange = debounce((e) => {
    setSearchTerm(e.target.value);  // Оновлюємо значення пошукового терміну
  }, 300);  // 300 мс затримка перед виконанням запиту

  const handleCarClick = (carId) => {
    window.location.href = `/item/${carId}`;
  };

  return (
    <div className="container">
      <h2>Каталог автомобілів</h2>

      {/* Панель пошуку */}
      <input
        type="text"
        placeholder="Пошук..."
        value={searchTerm}
        onChange={handleSearchChange}  // Оновлюємо значення пошуку при введенні
      />

      <div className="catalog-grid">
        {filteredCars.length > 0 ? (
          filteredCars.map((car) => (
            <div key={car.id} className="car-card" onClick={() => handleCarClick(car.id)}>
              <h3>{car.brand} {car.model}</h3>
              <p>Макс. швидкість: {car.maxSpeed} км/год</p>
              <img src={`http://localhost:5008${car.image}`} alt={car.model} />
            </div>
          ))
        ) : (
          <p>Нічого не знайдено</p>
        )}
      </div>
    </div>
  );
};

export default Catalog;
